import React,{useState} from 'react'

export default function Chat() { 
  const [cal , setcal] = useState({});

  const add = ()=>{
    setcal(prevCal  => prevCal + 1)    
  }
  const subt = ()=>{
    if(cal > 0  )
    {
      setcal(prevCal => prevCal - 1)
    }
    else
    {
      setcal(false)
    }

  }

  return (
    <div>
      <button onClick={add}>add</button>
      <p>{cal}</p>
      <button onClick={subt}>sub</button>
    </div>
  )
}
