import React , {useState} from 'react'
import {Link} from 'react-router-dom'


export default function Join() {
    const [name , setName] = useState('');
    const [room , setRoom] = useState('');
  return (
    <div className='container h-full w-full border-2 border-red-600 flex items-center justify-center flex-col'>
        <div className="container border-2 border-yellow-500 h-3/4 w-10/12  flex items-center justify-start flex-col">
            <h1 className='p-5 my-5 text-center text-5xl text-green-500 border-2'>Halal Chat</h1>
            <div className="container h-3/4 w-10/12 border-2 flex flex-col items-center justify-center">
                <input onChange={(Event)=> Event.target} type="text" className='p-2 m-4 w-80  text-xl bg-transparent border-b-2 border-green-500 focus:outline-green-500 focus:outline-none focus:border-none ' placeholder='Enter Name'/>
                <input onChange={''} type="text" className='p-2 m-4 w-80 text-xl bg-transparent border-b-2 border-green-500 focus:outline-none focus:outline-green-500 focus:border-none' placeholder='Enter Name'/>
                <Link to={'/'}><button type='submit'  className='bg-transparent text-xl  hover:bg-green-500 hover:border-green-500  hover:text-white cursor-pointer transition-all duration-2000 p-3 rounded-md my-4  border-2 w-36 '>Join Chat</button></Link>
            </div>
        </div>
    </div>
  )
}
